#SharedVideosNormalization
import re
import json

trace_level = 0 #default level: warning

constExcludeFromCoveo = 'exclude from Coveo'

brands_mapping = {
    #lower_case_input_match: output_value
    'masterBetty': 'masterBetty',
    'dummy': 'dummy',
    'dsadsa': 'dsadsa',
    'clicker' : 'Clicker'
}

product_type_mapping = {
    #lower_case_input_match: output_value
    'can': 'can',
    'can accessories': 'can Accessories'
}

def main():
    log_trace('ENTER: main')

    normalize('ytvideotags','commonbrand', brands_mapping)
    normalize('ytvideotags','commonproducttype', product_type_mapping)

    log_trace('EXIT: main')

#normalize
# input: inputField, outputField, valuemap, valuekey
# output: "commonbrand" metadata
def normalize(metainput, metaoutput, mapping):
    original_meta = safe_get_meta_data_value(metainput)
    log_trace('original_meta: ' + original_meta)

    lowercase_meta = original_meta.lower()
    log_trace('lowercase_meta: ' + lowercase_meta)

    meta_list = lowercase_meta.split(';')

    normalized_list = [] #default use empty

    for meta_value in meta_list:
        log_trace('meta_value: ' + meta_value)
        if meta_value in mapping:
            normalized_value = mapping[meta_value]
            normalized_list.append(normalized_value)
            log_trace('found map for ' + meta_value + ':' + normalized_value)
        else:
            log_trace('not found map for ' + meta_value)
    
    normalized_meta = ';'.join(normalized_list)
    document.add_meta_data({metaoutput: normalized_meta})
    return normalized_meta

#safe_get_parameter
#  returns value from Indexing Pipeline Extension "Parameter"
#  returns a string type by default
#  returns a value of same tyep as defaultValue param, if cast is possible
#    if case impossible, returns defaultValue
def safe_get_parameter(paramName, defaultValue=None):
    def func_get(p_name):
        return parameters[p_name]
        
    return safe_get_data(func_get, paramName, defaultValue)

def safe_get_meta_data_value(metadataName, defaultValue=None):
    def func_get(p_name):
        return document.get_meta_data_value(p_name)[-1]

    log_trace('ENTER  safe_get_meta_data_value:' + metadataName)
    retval =  safe_get_data(func_get, metadataName, defaultValue)
    log_trace('EXIT safe_get_meta_data_value:' + retval)
    return retval

def safe_get_data(func, paramName, defaultValue=None):
    try:
        if defaultValue is None:
            defaultValue = ''

        value = func(paramName)
        neededType = type(defaultValue)
        log_trace('{0} {1}: {2}'.format(paramName, type(value), value))
        
        return neededType(value)
    except Exception as e:
        #log_trace('except in safe_get_data: ' + str(e))
        return defaultValue #return the default on any error

def log_trace(msg):
    global trace_level
    if trace_level < 1:
        log(msg, 'debug')

def log_info(msg):
    global trace_level
    if trace_level < 2:
        log(msg, 'normal')
        
def log_warn(msg):
    global trace_level
    if trace_level < 3:
        log(msg, 'warning')

def log_error(msg):
    log(msg, 'error')        

log_trace('ENTER SharedVideosNormalization')
trace_level = safe_get_parameter('trace_level', trace_level)

try:
    main()
except Exception as e:
    log_error('UNEXPECTED: ' + str(e))
log_trace('EXIT SharedVideosNormalization')
